import React, { useRef } from 'react';
import { StyleSheet, Text, View, Image, TouchableOpacity, Button, Platform, Share, Dimensions  } from 'react-native';
import axios from 'axios';
import ViewShot from 'react-native-view-shot';
import Toast from 'react-native-toast-message';
import Partager from '../../img/partager.png';

const ScreenFinal = ({route}) => {
  const GenererChaineAleatoire = longueur => {
    let listeCar = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let chaine = '';
    let max = listeCar.length - 1;
    for (let i = 0; i < longueur; i++) {
    chaine = chaine + listeCar[Math.floor(Math.random() * (max + 1) )];
    }
    return chaine;
  }

    const { photo } = route.params;
    let lol = photo.base64
    console.log(photo.uri)
  console.log(lol)
    async function Test() {
      let token_url = GenererChaineAleatoire(20)
      await axios({
        method: 'POST',
        data: 'img='+lol+'&token_url='+token_url,
        url:'https://www.adrien-meynard.fr/dessin/post_api/'
      })
      .then((response) => {
        if(response.data === 1) {
          Toast.show({
            type: 'success',
            text1: 'Lien de l\'image copié dans le presse papier',
          });
        }
      })
    }
    Test()

    let viewShotRef = useRef()
    async function captureAndShareScreenshot() {
      const imageUri = await viewShotRef.current.capture()
      
        // if(Platform.OS === 'ios') {
          Share.share({url: imageUri})  
        // } else {
          
        //   } else {
        //     Toast.show({
        //       type: 'error',
        //       text1: 'Erreur : merci de rééssayez plus tard',
        //     });          
        //   }
        // })
        // .catch((err) => {
        //   Toast.show({
        //     type: 'error',
        //     text1: err + ' :  merci de rééssayez plus tard',
        //   });          
        // })
      // }
    }
  return (
    <>
      <ViewShot
        ref={viewShotRef}
        options={{quality: 1}}
        style={{zIndex:9999}} > 

          <Image source={{ uri: `data:image/png;base64,${lol}` }} style={{width:100+'%',height:100+'%'}}/>   
            <TouchableOpacity
            style={styles.btn}
            onPress={captureAndShareScreenshot}
          >

            <Text style={styles.textBtn}><Image source={Partager} style={{width:70,height:70}} /></Text>
          </TouchableOpacity>
      </ViewShot>
      <Toast />
    </>
  );
}

const styles = StyleSheet.create({
  background: {
    width:100+'%',
    height:100+'%',
  },
  btn: {
    position:'absolute',
    top:87+'%',
    left:25,
  },

  textBtn: {
    color: 'white',
    fontSize: 20,
    textAlign: 'center',
    backgroundColor:'white',
    borderWidth: 1,
    borderRadius:20,
    borderColor: '#fff',
    overflow:'hidden',
  },
})
export default ScreenFinal
