import { NativeModules, Platform } from 'react-native';

export const getHex = (path, options) => new Promise((resolve, reject) => {
  if (Platform.Os === 'ios') {
    NativeModules.RNPixelColor.getHex(path, options, (err, color) => {
      if (err) {
        return reject(err);
      }
      resolve(color);
    });
  } else {
    const { x, y } = options;
    console.log("ANDROID calling module");
    console.log(NativeModules.RNPixelColor.getName());
    NativeModules.RNPixelColor.getPixelRGBAofImage(imageName, x, y, (err, color) => {
      if (err) {
        console.log(err);
        return reject(err);
      }
      console.log(color);
      resolve(color);
    });
  }
});

export default {
  getHex,
}
