import React, { useRef } from 'react';
import { StyleSheet, Text, View, Image, TouchableOpacity, Button, Platform, Share, Dimensions  } from 'react-native';
import axios from 'axios';
import ViewShot from 'react-native-view-shot';
const ScreenFinal = () => {
  const GenererChaineAleatoire = longueur => {
    let listeCar = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let chaine = '';
    let max = listeCar.length - 1;
    for (let i = 0; i < longueur; i++) {
    chaine = chaine + listeCar[Math.floor(Math.random() * (max + 1) )];
    }
    return chaine;
  }

    // const { photo } = route.params;

    let viewShotRef = useRef()
    async function captureAndShareScreenshot() {
      const imageUri = await viewShotRef.current.capture()
      if(Platform.OS === 'ios') {
        Share.share({url: imageUri})  
      } else {
        let token_url = GenererChaineAleatoire(20)
        await axios({
          method: 'POST',
          data: 'img='+imageUri+'&token_url='+token_url,
          url:'https://www.adrien-meynard.fr/dessin/post_api/'
        })
        .then((response) => {
          if(response.data === 1) {
            alert('Good')
            // adrien-meynard.fr/dessin/?token_img=PdKnYozcAHIqXQxYIbjp
          }else {
            alert('No good')
          }
        })
        .catch((err) => {
          console.log(err)
          alert('Erreur')
        })
      }
    };

  return (
          // <Image source={{ uri: photo.uri }} style={{width:100+'%',height:100+'%'}}/> 
// peut etre a changer en fonction de la plateforme : si ios ou android ---- ios = "" 
    <ViewShot
      ref={viewShotRef}
      options={{quality: 1, result:'base64'}}> 

      <TouchableOpacity
        style={styles.btn}
        onPress={captureAndShareScreenshot}
      >
        <Text style={styles.textBtn}>SHARE</Text>
      </TouchableOpacity>

    </ViewShot>
  );
}

const styles = StyleSheet.create({
  btn: {
    zIndex:9,
    padding: 10,
    width: 100 + '%',
    marginBottom: 5,
    backgroundColor:'green',
    position:'absolute',
    top:Dimensions.get('window').height - 175, 
  },

  textBtn: {
    color: 'white',
    fontSize: 20,
    textAlign: 'center',
  },
})
export default ScreenFinal
