  //   //   })
  // }
  return (
    <ViewShot
        style={styles.container}
        ref={aaaa}
        options={{format: 'jpg', quality: 0.9}}>
        <View>
          <Button
            style={styles.btn}
            title="Capture and Share"
            onPress={this.captureAndShareScreenshot}
          />
        </View>
    </ViewShot>
    //<Image source={{ uri: photo.uri }} style={{width:100+'%',height:100+'%'}}/>
  );
}


const styles = StyleSheet.create({
  btn: {
