import React, {useState} from "react";
import { StyleSheet, View, Text } from "react-native";

const Loading = () => {
    return (
        <>  
            <Text>Salut</Text>
        </> 
    )
}

export default Loading

const styles = StyleSheet.create({

    loading: {
    width:100+'%',
    height:100+'%',
    zIndex:999,
    backgroundColor:"gray",
    opacity: 0.8,
    top:0,
    left:0
  },

  loadingSvg: {
    position: 'absolute',
    top: 50+'%',
    left: 50+'%',
    opacity: 1,
    zIndex: 99999,
  },

  none: {
      display:'none'
  }

})
