import * as React from 'react';
import { StyleSheet, Text, View, Image, TouchableOpacity } from 'react-native';
import axios from 'axios';

const ScreenFinal = () => {
  //  const { photo } = route.params;
  const Post_img = async () => {
    await axios({
      method: 'GET',
      url: url_api,
      // data:{param1:'PARAMETRE API ICI'}
    })
      .then((response) => {
        setStatusFormCss('green')
        setStatusForm('Formulaire envoyé !')
        setEmail('')
        setName('')

      })
      .catch((err) => {
        console.log(err)
        setStatusForm('Erreur formulaire ! : ' + err)
      })
  }
  return (
    <TouchableOpacity
      style={styles.btn}
      onPress={Post_img}
    >
      <Text style={styles.textBtn}>Subscribe</Text>

    </TouchableOpacity>
    //<Image source={{ uri: photo.uri }} style={{width:100+'%',height:100+'%'}}/>
  );
}


const styles = StyleSheet.create({
  global: {
    margin: 20,
    flex: 1,
    justifyContent: 'flex-start',
  },
  input: {
    marginTop: 8,
    marginBottom: 3,
    width: 100 + '%',
    borderRadius: 10,
    paddingLeft: 15,
    paddingTop: 10,
    paddingBottom: 10,
    borderWidth: 2,
    borderColor: 'gray',
    fontSize: 18,
  },

  btn: {
    backgroundColor: 'green',
    padding: 10,
    width: 100 + '%',
    borderRadius: 5,
    marginBottom: 5
  },

  textBtn: {
    color: 'white',
    fontSize: 20,
    textAlign: 'center',
  },

  text: {
    fontSize: 20,
    marginTop: 10
  },
})
export default ScreenFinal
