import React, {useState} from "react";
import { Text, StyleSheet, View, TextInput, TouchableOpacity,Platform } from "react-native";
import Title from "../assets/title";
import axios from 'axios';

const NewsLetter = () => {

  const [email, setEmail] = useState('')
  const [name, setName] = useState('')

  const [errEmail, setErrEmail] = useState('')
  const [errName, setErrName] = useState('')

  const [statusForm, setStatusForm] = useState('')
  const [statusFormCss, setStatusFormCss] = useState('')

  let url_api = 'https://jsonplaceholder.typicode.com/todos/1'
  const regex =  /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-])/;
  const addUser = async() => {
    setErrEmail('')
    setErrName('')
    setStatusForm('')
    setStatusFormCss('red')

    let err = 0
    if(email === '') {
      setErrEmail('Champ vide')
      err = 1
    } else if(!email.match(regex)){
      setErrEmail('Email non valide')
      err = 1
      console.log('slaut')
    }

    if(name === '') {
      setErrName('Champ vide')
      err = 1
    }

    if(err != 0) {
      setStatusForm('Erreur formulaire')
    } else {
      await axios({
        method: 'GET',
        url: url_api,
        // data:{param1:'PARAMETRE API ICI'}
      })
      .then((response) => {
        setStatusFormCss('green')
        setStatusForm('Formulaire envoyé !')
        setEmail('')
        setName('')

      })
      .catch((err) => {
        console.log(err)
        setStatusForm('Erreur formulaire ! : ' + err)
      })
    } 
  }
    return (
        <View style={styles.global}>
          <Title texte="Subscription" />
          <Text style={styles.text}>Email</Text>
          <TextInput
            style={styles.input}
            onChangeText={(e) => setEmail(e)}
            value={email}
            placeholder="email"
            keyboardType="text"
          />
          <Text style={[styles.btnMargin, styles.err]}>{errEmail}</Text>
          <Text style={styles.text}>FirstName</Text>
          <TextInput
            style={styles.input}
            onChangeText={(e) => setName(e)}
            value={name}
            placeholder="firstname"
            keyboardType="text"
          />    
          <Text style={[styles.btnMargin, styles.err, styles.btnMarginTwo]}>{errName}</Text>

          <TouchableOpacity
            style={styles.btn}
            onPress={addUser}
          >
            <Text style={styles.textBtn}>Subscribe</Text>
          </TouchableOpacity>
          <Text style={[styles.btnMargin,styles.btnMarginTop, styles.err], {color:statusFormCss}}>{statusForm}</Text>

        </View>
    )
}

const styles = StyleSheet.create({
    global: {
    margin:20,
    flex:1,
    justifyContent:'flex-start',
  },
  input: {
    marginTop:8,
    marginBottom:3,
    width: 100+'%',
    borderRadius: 10,
    paddingLeft:15,
    paddingTop:10,
    paddingBottom:10,
    borderWidth:2,
    borderColor:'gray',
    fontSize:18,
  },

  btn: {
    backgroundColor:'green',
    padding:10,
    width:100+'%',
    borderRadius: 5,
    marginBottom:5
  },

  textBtn: {
    color:'white',
    fontSize:20,
    textAlign:'center',
  },

  text: {
    fontSize:20,
    marginTop:10
  },

  btnMargin: {
    marginBottom:5,
    ...Platform.select({
      ios: {
        marginBottom:5,
      }
    })
  },

  btnMarginTwo: {
        marginBottom:25,
    ...Platform.select({
      ios: {
        marginBottom:35,
      }
    })
  },

  btnMarginTop: {
    marginTop:3
  },

  err: {
    fontSize:15,
    color:'red',
    height:18,
    ...Platform.select({
      ios: {
        height:18
      }
    })
  },
})

export default NewsLetter
